import React, { useState } from "react";
import './AddForm.css';

export default function AddForm() {
    const [todoValue, setTodoValue] = useState('');

    const handleChange = (event) => {
        setTodoValue(event.target.value);
    }

    return (
        <div className='addForm_container'>
        <form className='addForm' onSubmit="">
          <label className='addForm_label' htmlFor='new-todo'>To Do{' '}</label>
          <input className='addForm_input' type='text' id='new-todo' name='new-todo' onChange={handleChange}></input>

          <label className='addForm_label' htmlFor='new-todo'>Detail{' '}</label>
          <input className='addForm_input' type='text' id='new-todo' name='new-todo' onChange={handleChange}></input>

          <button>Attach</button>
        </form>
      </div>

    )
}
